void set_catapult()
{
	if (!getBumperValue(bumper))
	{
		setMotorSpeed(catapultMotor, 100);
		wait1Msec(500);
		waitUntil(getBumperValue(bumper));
		setMotorSpeed(catapultMotor, 0);
	}
}

void fire_catapult()
{
	if (getBumperValue(bumper))
	{
		setMotorSpeed(catapultMotor, 100);
		wait1Msec(300);
		setMotorSpeed(catapultMotor, 0);
	}
}

void eight_ball()
{
	turnTo(15, 8);
	setMotorSpeed(intakeMotor, 100);
	strafePID(20, 90, 5, 0, 1, 3);
	//strafePID(-20, 90, 5, 0, 1, 3);
	turnTo(-50, 10);
	//intake on midway
	strafePID(20, 90, 5, 0, 1, 3);
	//strafePID(-5, 90, 5, 0, 1, 3);
	turnTo(-90, 5);
	desired_heading = -90;
	//waitUntil(getTouchLEDValue(LED) == 1);

	strafePID(70, 90, 5, 2, 1, 5);
	strafePID(20, 60, 5, 2, 1, 5);
	turnTo(-30, 3);
	strafePID(40, 90, 5, 2, 1, 15);
	desired_heading = -10;
	strafePID(100, 90, 5, 2, 1, 5);

	turnTo(70, 3);
	strafePID(40, 90, 5, 2, 1, 5);//68 5
	strafePID(-10, 30, 5, 0, 1, 2);
	turnTo(180, 10);
	strafePID(68, 90, 5, 2, 1, 5);
	turnTo(270, 5);

	setTouchLEDRGB(LED, 0, 255, 50);
	// for speed 90 use 5, 0, 1
	// for speed 30 use 5, 2, 1
}


